import { useState, useEffect } from "react";
import ColorList from "./components/ColorList";
import Color from "./components/Color";


function App() {
  //Need state for Winning Color (RGB) , and 6 other colors
  const NUMCOLORS = 6;
  const newColors = [];
  const [winningIndex, setWinningIndex] = useState("");
  const [gameColors, setGameColors] = useState([]);
  const [isWinner, setIsWinner] = useState(false);




  function getRandomRGB() {
    return Math.floor(Math.random() * 256);
  }


  function getNewColor() {
    let newColor = {
      red: getRandomRGB(),
      green: getRandomRGB(),
      blue: getRandomRGB(),
    };
    return newColor;
  }

  function startGame() {

    setWinningIndex(Math.floor(Math.random() * NUMCOLORS));

    for (let i = 0; i < NUMCOLORS; i++) {
      newColors.push(getNewColor());
    }
    // setGameColors([...gameColors, ...newColors]);
  }


  useEffect(() => {
    startGame();
    setGameColors([newColors]);
  }, []);



  console.log(winningIndex);
  console.log(gameColors);
  return <div className="App">
    {gameColors.map((color) => {
      <div>{color.red}</div>
    })}
 </div>
}

export default App;
