import { useState, useEffect } from "react";

function Color({color, handleIsWinner, index, winnerClicked}) {
  const [wasClicked, setWasClicked] = useState(false)
  function handleClickStatus () {
    if (wasClicked) {
      setWasClicked(true)
    }
  }
  return <div id={`item-${index}`} onClick={() => handleIsWinner(color)} style={{backgroundColor: `rgb(${color.red}, ${color.green},${color.blue} )`}}>{(wasClicked && !color.isWinner) ? 'Try Again!' : ''}</div>;
}

export default Color;
