import { useState, useEffect } from "react";
import Color from "./Color";
import Winner from "./Winner";
function ColorList({ gameColors, winningColor }) {
    function handleIsWinner(color) {
        if (color.isWinner) {
          console.log('I am winner')
        } else {
          console.log('not me')
        }
      }

  return (
    <div>
      {gameColors.map((color, index) => (
        <div key={index}>
          <Color color={color} winningColor={winningColor} onClick={() => handleIsWinner()}/>
        </div>
      ))}
      <Winner winningColor={winningColor} />
    </div>
  );
}

export default ColorList;
