import { useState, useEffect } from "react";
import Color from "./Color";
import Winner from "./Winner";
function ColorList({ gameColors, winningColor, startGame }) {
    const [winnerClicked, setWinnerClicked] = useState(false)

    function handleIsWinner(color) {
        if (color.isWinner) {
          console.log('I am winner')
          setWinnerClicked(true)
        } else {
          console.log('not me')
        }
      }

  return (
    <div>
      {gameColors.map((color, index) => (
        <div key={index} className={`item-${index}`}>
          <Color color={color} winningColor={winningColor} handleIsWinner={handleIsWinner}/>
        </div>
      ))}
      <Winner winningColor={winningColor} winnerClicked={winnerClicked} startGame={startGame}/>
    </div>
  );
}

export default ColorList;
