import { useState, useEffect } from "react";
import ColorList from "./components/ColorList";
import Color from "./components/Color";


function App() {
  //Need state for Winning Color (RGB) , and 6 other colors
  const NUMCOLORS = 6;
  const [winningIndex, setWinningIndex] = useState(0);
  const [gameColors, setGameColors] = useState([]);
  const [isWinner, setIsWinner] = useState(false);
  const [isStarted, setIsStarted] = useState(true)



  function getRandomRGB() {
    return Math.floor(Math.random() * 256);
  }


  function getNewColor() {
    let newColor = {
      red: getRandomRGB(),
      green: getRandomRGB(),
      blue: getRandomRGB(),
    };
    return newColor;
  }

  function startGame() {
    const newColors = [];


    for (let i = 0; i < 6; i++) {
      newColors.push(getNewColor());
    }
    setWinningIndex(Math.floor(Math.random() * 6));
    setGameColors([newColors]);
    setIsStarted(true)
  }




  return <div className="App">
    {isStarted ? ( <button onClick={startGame()}>Start Game</button>) : (<><div>
    {gameColors.map((color, index) => {
    <div  key ={index}><Color color={color}/></div>
  })}</div></>)}
 </div>
}

export default App;
